import { useEffect, useState } from 'react';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer
} from 'recharts';
import './Performance.css';

function Performance() {
  const [chartData, setChartData] = useState([]);
  const [strengths, setStrengths] = useState([]);
  const [improvements, setImprovements] = useState([]);

  useEffect(() => {
    const fetchPerformance = async () => {
      try {
        const res = await fetch('http://localhost:5000/api/performance/123'); // Replace with actual user ID
        const data = await res.json();

        // Merge sessions and skills by date
        const combinedMap = new Map();

        data.sessions.forEach(session => {
          const key = session.date;
          combinedMap.set(key, {
            date: key,
            avgRelevance: session.avgRelevance
          });
        });

        if (data.skills) {
          data.skills.forEach(skill => {
            const key = skill.date;
            const existing = combinedMap.get(key) || { date: key };
            existing.skillScore = skill.score;
            combinedMap.set(key, existing);
          });
        }

        const mergedData = Array.from(combinedMap.values()).sort(
          (a, b) => new Date(a.date) - new Date(b.date)
        );

        setChartData(mergedData);
        setStrengths(data.strengths || []);
        setImprovements(data.improvements || []);
      } catch (err) {
        console.error('Error fetching performance:', err);
      }
    };

    fetchPerformance();
  }, []);

  return (
    <div className="performance-wrapper">
      <h2>üìà Your Growth Over Time</h2>

      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={chartData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="date" />
          <YAxis domain={[0, 100]} />
          <Tooltip />
          <Legend />
          <Line
            type="monotone"
            dataKey="avgRelevance"
            stroke="#00BFA6"
            name="Interview Relevance"
          />
          <Line
            type="monotone"
            dataKey="skillScore"
            stroke="#FF6F61"
            name="Skill Test Score"
          />
        </LineChart>
      </ResponsiveContainer>

      <div className="summary-section">
        <h3>‚úÖ Strengths</h3>
        <ul>
          {strengths.map((s, i) => (
            <li key={i}>üü¢ {s}</li>
          ))}
        </ul>

        <h3>‚ö†Ô∏è Areas to Improve</h3>
        <ul>
          {improvements.map((i, j) => (
            <li key={j}>üî¥ {i}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Performance;
