import { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import './Questions.css';

function Questions() {
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const query = new URLSearchParams(useLocation().search);
  const role = query.get('role');
  const level = query.get('level');

  useEffect(() => {
    const fetchQuestions = async () => {
      try {
        const res = await fetch(
          `http://localhost:5000/api/questions?role=${role}&level=${level}`
        );
        const data = await res.json();

        if (Array.isArray(data)) {
          setQuestions(data);
        } else {
          setQuestions([]);
          setError('Unexpected response format. No questions found.');
        }
      } catch (err) {
        console.error('Fetch error:', err);
        setError('Failed to load questions. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchQuestions();
  }, [role, level]);

  return (
    <div className="questions-wrapper">
      <h2>{role} Test ({level})</h2>

      {loading && <p>Loading questions...</p>}

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {!loading && !error && questions.length === 0 && (
        <div className="empty-state">
          <p>No questions available for this role and level yet.</p>
          <p>Please check back later or try a different combination.</p>
        </div>
      )}

      {!loading && !error && questions.length > 0 && (
        questions.map((q, i) => (
          <div key={i} className="question-card">
            <p><strong>Q{i + 1}:</strong> {q.question}</p>
            <ul>
              {Array.isArray(q.options) ? (
                q.options.map((opt, idx) => (
                  <li key={idx}>{opt}</li>
                ))
              ) : (
                <li style={{ color: 'red' }}>Invalid options format</li>
              )}
            </ul>
          </div>
        ))
      )}
    </div>
  );
}

export default Questions;
